import { useRef } from "react";

export default function SearchBar({ searchTerm, setSearchTerm }) {
  const lastChange = useRef();
  const searchBar = useRef();

  // DEBOUNCING: only change searchTerm once user stops typing after specified time in setTimeout
  // reduces amount of Spotify API calls (i.e. not after every key stroke)
  const handleOnChange = (value) => {
    if (lastChange.current) {
      clearTimeout(lastChange.current);
    }

    lastChange.current = setTimeout(() => {
      lastChange.current = null;
      setSearchTerm(value);
    }, 2000);
  };


  return (
    <>
      <div className=" flex justify-center">
        <input
          className={`rounded-3xl bg-default text-center w-36 h-8 sm:w-48 sm:h-11 text-mobile-2 sm:text-sm-screen-1 mb-5 sm:mb-10 p-2 caret-foreground search-cancel:appearance-none search-cancel:w-2 sm:search-cancel:w-3 search-cancel:h-2 sm:search-cancel:h-3 search-cancel:bg-contain 
            search-cancel:bg-[url(data:image/svg+xml;base64,)]`}
          ref={searchBar}
          type="search"
          name="search-form"
          id="search-form"
          onChange={(e) => handleOnChange(e.target.value)}
          placeholder={searchTerm ? searchTerm : "Search Spotify..."}
        />
      </div>
    </>
  );
}
